import React, { useState } from 'react';
import './App.css';
import Person from './Person/Person'

const App = () => {
  const [ personsState, setPersonsSate ] = useState({
    persons: [
      { name: 'Ryan', age: 28},
      { name: 'Lucy', age: 2},
      { name: 'Rachael', age: 23}
    ],
    otherState: 'some other value'

  });

    const switchHandler = () => {
      //Don't do this personsState.persons[0].name = 'Ryan the beautiful'
      this.setState({
        persons: [
          { name: 'Ryan the beautiful', age: 28},
          { name: 'Lucy the brave', age: 2},
          { name: 'Rachael the pretty', age: 23}
        ]
      })
    }

    return (
      <div className="App">
        <h1>Hi, I'm a React App</h1>
        <p>This is really working!</p>
        <button onClick={switchHandler}>Switch Name</button>
        <Person name={personsState.persons[0].name} age={personsState.persons[0].age}>My hobbies: wedding planning</Person>
        <Person name={personsState.persons[1].name} age={personsState.persons[1].age}>My hobbies: wedding planning</Person>
        <Person name={personsState.persons[2].name} age={personsState.persons[2].age}>My hobbies: wedding planning</Person>
      </div>
    );
    // return React.createElement('div', {className: 'App'}, React.createElement('h1', null, 'Hi, I\'m a React App!'))

}

export default App;


