    { name: 'Rachael', age: 23}
  ],
  otherState: 'some other value'
}

switchHandler = () => {
  //Don't do this personsState.persons[0].name = 'Ryan the beautiful'
  this.setState({
    persons: [
      { name: 'Ryan the beautiful', age: 28},
      { name: 'Lucy the brave', age: 2},
      { name: 'Rachael the pretty', age: 23}
    ]
  })
}
